rowLayout.rollMessage:
%{
    if (${item._ability_cost}$ <= ${resource_current}$) {
      if ('${escapeQuotes(item._ability_tier_1)}$' != '') {
        return await game.macros.getName('Power Roll!').execute({
        character_name:'${escapeQuotes(name)}$',
        defaultSituationalBonus:0,
        ability_title:'${escapeQuotes(item.name)}$',
        resource_current:${resource_current}$,
        tier_1_result:'${escapeQuotes(item._ability_tier_1)}$',
        tier_2_result:'${escapeQuotes(item._ability_tier_2)}$',
        tier_3_result:'${escapeQuotes(item._ability_tier_3)}$',
        power_effect:'${escapeQuotes(item._ability_power_effect)}$',
        defaultAbility:'${item._ability_power_roll}$',
        might_score:${fetchFromActor(name,'might_score')}$,
        agility_score:${fetchFromActor(name,'agility_score')}$,
        reason_score:${fetchFromActor(name,'reason_score')}$,
        intuition_score:${fetchFromActor(name,'intuition_score')}$,
        presence_score:${fetchFromActor(name,'presence_score')}$
        });
      } else {

        rollMode = game.settings.get("core", "rollMode");
        gmUserIds = game.users.filter(user => user.isGM).map(user => user.id);
        messageData = {
          flavor: `<h2><strong>${escapeQuotes(item.name)}$</h2></strong>`,
          user: game.user._id,
          speaker: {
            alias: '${escapeQuotes(name)}$',
            actor: game.actors.getName('${escapeQuotes(name)}$')
          },
          content: "${escapeQuotes(item._ability_power_effect)}$"
        };

        if (rollMode === "gmroll" || rollMode === "blindroll" || rollMode === "selfroll") {
          messageData.whisper = gmUserIds;
        } else if (rollMode === "publicroll") {
          messageData.whisper = [];
        };

        ChatMessage.create(messageData);

      };
    }
  }%

${(resource_current < item._ability_cost) ? notify('error','Not enough VP') : false}$
